library(purrr)
library(dplyr)

windw <- function(x,start = 1,end=length(x),size=14){
  starts <- 1:((length(x)-size)+1)
  ends <- size:length(x)
  map2(starts,ends,\(start,end) x[start:end])
}

char_before_msg<-function(msg,msg_mrk_ln){
  val<-strsplit(msg,split = "")
  windw(val[[1]],size = msg_mrk_ln) |>
  map(\(cur_set){
    imap_lgl(cur_set,\(cur_elem,cur_elem_index){
      if(cur_elem %in% cur_set[-cur_elem_index]) T else F
    })
  })|>
    map(any)|>
    detect_index(\(x)x==F)|>
    map_dbl(\(result) result+(msg_mrk_ln-1))
}

# first riddle
exa <- "
"
char_before_msg(exa,4)

# second riddle
char_before_msg(exa,14)

